    )

    return (
      <div className="App">
      <input 
        type='search' 
        placeholder='search monsters' 
        onChange={ e => this.setState({ searchField: e.target.value }) }
      />
        <CardList monsters={ this.state.monsters } />         
      </div>
    );    
  }
}

export default App;
